<script type="text/javascript">
jQuery.noConflict();
;(function($) {
var item = $('#gear_control .roll .rollt');
$('#gear_control .roll').scrollLeft(0);
$('#gear_control .roll .inner').width(
$('#gear_control .roll .rollt').width() * $('#gear_control .roll .rollt').length
);
$('#prev').click(function(){
$('#gear_control .roll').stop(1,1).animate({
'scrollLeft': $('#gear_control .roll').scrollLeft() + item.outerWidth(1)
}, 200);
});
$('#next').click(function(){
$('#gear_control .roll').stop(1,1).animate({
'scrollLeft': $('#gear_control .roll').scrollLeft() - item.outerWidth(1)
}, 200);
});
})(jQuery);
</script>
